    public static class AchievementService
    {
        static Queue<Popup> WaitQueue = new Queue<Popup>();
        public static void Test()
        {
            Popup popup = new Popup()
            {
                Child = new AchievementBox(new Achievement() { Tilte = "绅士之魂", IconData = AchievementIcons.Hat, Description = "解锁ExOnly分级.",Type = AchievementType.Silver }),
            };
            popup.Closed += popup_Closed;
            if(WaitQueue.Count == 0)
            {
                popup.IsOpen = true;
            }
            WaitQueue.Enqueue(popup);
        }

        static void popup_Closed(object sender, EventArgs e)
        {
            WaitQueue.Dequeue();
            if(WaitQueue.Count != 0)
            {
                WaitQueue.Peek().IsOpen = true;
            }
        }
    }

